// Custom Scroll
$(function() {
	$('.customScroll').slimScroll({
		height: "300px",
		color: '#e5e8f2',
		alwaysVisible: false,
		size: "3px",
		distance: '2px',
		railVisible: false,
		railColor: "#0066bf",
	});
});

